import 'package:time_tracking_dashboard/data/core/api_client.dart';import 'package:time_tracking_dashboard/data/core/api_constants.dart';import 'package:time_tracking_dashboard/data/models/time_tracking_model.dart';abstract class DashboardRemoteDataSource {  Future<List<TimeTrackingModel>> fetchTimeTrackingData();}class DashboardRemoteDataSourceImpl extends DashboardRemoteDataSource {  ApiClient apiClient;  DashboardRemoteDataSourceImpl({required this.apiClient});  @override  Future<List<TimeTrackingModel>> fetchTimeTrackingData() async {    final timeTrackingResponse = await apiClient.get(ApiConstants.timeTracking);    // convert list of json to list of [TimeTrackingModel]    List<TimeTrackingModel> timeTrackingModels = List<TimeTrackingModel>.from(        timeTrackingResponse.map((e) => TimeTrackingModel.fromJson(e)));    return timeTrackingModels;  }}