import 'package:dartz/dartz.dart';import 'package:time_tracking_dashboard/domain/entities/app_error.dart';import 'package:time_tracking_dashboard/domain/entities/no_params.dart';import 'package:time_tracking_dashboard/domain/entities/time_tracking_entity.dart';import 'package:time_tracking_dashboard/domain/repositories/dashboard_repositories.dart';import 'package:time_tracking_dashboard/domain/usecases/use_cases.dart';class FetchTimeTrackingData    extends UseCase<List<TimeTrackingEntity>, NoParams> {  final DashboardRepository _dashboardRepository;  FetchTimeTrackingData(this._dashboardRepository);  @override  Future<Either<AppError, List<TimeTrackingEntity>>> call(          NoParams params) async =>      await _dashboardRepository.fetchTimeTrackingData();}