import 'package:flutter/material.dart';import 'package:time_tracking_dashboard/presentation/themes/custom_colors.dart';import 'custom_card_background_image.dart';class CustomCard extends StatefulWidget {  final Color? color;  final double topPadding;  final double height;  final double width;  final double radius;  final Widget? child;  final String backgroundImage;  final bool isThereHover;  final Color hoverColor;  const CustomCard(      {Key? key,      this.color = Colors.white,      this.isThereHover = false,      this.topPadding = 0.0,      this.hoverColor = CustomColors.hoverDarkBlueColor,      this.height = 130,      this.width = double.infinity,      this.radius = 15,      this.backgroundImage = '',      this.child})      : super(key: key);  @override  State<CustomCard> createState() => _CustomCardState();}class _CustomCardState extends State<CustomCard> {  Color cardColor = Colors.white;  @override  void initState() {    cardColor = widget.color ?? Colors.white;    super.initState();  }  @override  Widget build(BuildContext context) {    return Padding(      padding: EdgeInsets.only(top: widget.topPadding),      child: Stack(children: [        MouseRegion(          onHover: (event) {            setState(() {              if (MediaQuery.of(context).size.width > 950) {                cardColor = CustomColors.hoverDarkBlueColor;              }            });          },          onExit: (event) {            setState(() {              cardColor = widget.color!;            });          },          child: Container(            decoration: BoxDecoration(                color: widget.isThereHover ? cardColor : widget.color,                borderRadius: BorderRadius.all(Radius.circular(widget.radius))),            width: widget.width,            height: widget.height,            child: widget.child ?? const SizedBox(),          ),        ),        widget.backgroundImage != ''            ? CustomCardBackgroundImage(                image: widget.backgroundImage,              )            : const SizedBox(),      ]),    );  }}