import 'package:flutter/material.dart';import 'package:flutter_svg/svg.dart';import 'package:time_tracking_dashboard/presentation/themes/custom_colors.dart';class CustomCardBackgroundImage extends StatelessWidget {  final String image;  const CustomCardBackgroundImage({Key? key, required this.image})      : super(key: key);  @override  Widget build(BuildContext context) {    return Positioned(      top: -5,      right: 15,      child: SvgPicture.asset(        'assets/svgs/$image',        color: CustomColors.veryDarkBlueColor.withOpacity(0.2),        width: 65,        height: 65,      ),    );  }}