import 'package:flutter/material.dart';import 'package:time_tracking_dashboard/presentation/themes/custom_colors.dart';import 'custom_card.dart';class CustomProfileCard extends StatelessWidget {  final Widget? child;  final Widget? bottomChild;  final double? width;  final double? height;  final double? bottomHeight;  const CustomProfileCard(      {Key? key,      this.child,      this.bottomChild,      this.width = double.infinity,      this.bottomHeight = 130,      this.height = 130})      : super(key: key);  @override  Widget build(BuildContext context) {    return Stack(      children: [        CustomCard(          height: bottomHeight ?? 130,          width: width ?? double.infinity,          topPadding: 50,          color: CustomColors.darkBlueColor,          child: bottomChild ?? const SizedBox(),        ),        CustomCard(          height: height ?? 130,          width: width ?? double.infinity,          color: CustomColors.blueColor,          child: child ?? const SizedBox(),        )      ],    );  }}