import 'package:flutter/material.dart';import 'package:time_tracking_dashboard/presentation/layouts/custom_card.dart';import 'package:time_tracking_dashboard/presentation/themes/custom_colors.dart';class CustomTimeCard extends StatelessWidget {  final Color backgroundColor;  final String image;  final double? height;  final double? width;  final Widget? child;  const CustomTimeCard({    Key? key,    required this.backgroundColor,    required this.image,    this.child,    this.width,    this.height,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return Stack(      children: [        CustomCard(          height: height ?? 130,          width: width ?? double.infinity,          backgroundImage: image,          color: backgroundColor,        ),        CustomCard(          isThereHover: true,          height: height ?? 130,          width: width ?? double.infinity,          topPadding: 33,          color: CustomColors.darkBlueColor,          child: child ?? const SizedBox(),        )      ],    );  }}