import 'package:flutter/material.dart';import 'package:time_tracking_dashboard/presentation/themes/custom_colors.dart';import 'package:time_tracking_dashboard/presentation/themes/custom_themes.dart';class PeriodTabItemWidget extends StatefulWidget {  final bool isActive;  final Function? onTab;  final String title;  const PeriodTabItemWidget(      {Key? key, required this.title, this.isActive = false, this.onTab})      : super(key: key);  @override  State<PeriodTabItemWidget> createState() => _PeriodTabItemWidgetState();}class _PeriodTabItemWidgetState extends State<PeriodTabItemWidget> {  Color textColor = CustomColors.paleBlueColor;  @override  Widget build(BuildContext context) {    return MediaQuery.of(context).size.width > 950        ? Padding(            padding: const EdgeInsets.only(top: 12.0, left: 30.0),            child: GestureDetector(              onTap: () {                widget.onTab == null ? null : widget.onTab!();              },              child: MouseRegion(                  onHover: (event) {                    setState(() {                      textColor = Colors.white;                    });                  },                  onExit: (event) {                    setState(() {                      textColor = CustomColors.paleBlueColor;                    });                  },                  child: Text(widget.title,                      textAlign: TextAlign.center,                      style: CustomThemes.subTitleStyle.copyWith(                          color: widget.isActive ? Colors.white : textColor))),            ),          )        : Expanded(            child: GestureDetector(              onTap: () {                widget.onTab == null ? null : widget.onTab!();              },              child: Text(widget.title,                  textAlign: TextAlign.center,                  style: CustomThemes.cardTitleStyle.copyWith(                      color: widget.isActive ? Colors.white : textColor)),            ),          );  }}